mapper.select:
SELECT post_no AS postNo, title AS title, text_md AS textMd, text_html AS textHtml, read_cnt AS readCnt, like_cnt AS likeCnt, hate_cnt AS hateCnt, category AS category, CASE WHEN TO_CHAR(SYSDATE,'YYYY/MM/DD') = TO_CHAR(reg_dt,'YYYY/MM/DD') THEN TO_CHAR(reg_dt,'HH24:MI') ELSE TO_CHAR(reg_dt,'YYYY/MM/DD') END AS regDt, mod_dt AS modDt, member_email AS memberEmail FROM post WHERE 1=1
AND category = 1
AND member_email = #{searchWord2}

mapper.update:
UPDATE post
SET like_cnt = like_cnt + 1
WHERE post_no = #{postNo}

UPDATE post
SET like_cnt = like_cnt - 1
WHERE post_no = #{postNo}
